import React, { useEffect, useState } from 'react';
import classes from './Blog.module.css';
import NavList from '../../components/NavList/NavList';
import axios from 'axios';
import Layout from '../../components/Layout/Layout';

function blog(props) {
  const [post, setPost] = useState(null);

  useEffect(() => {
    const background = document.getElementsByTagName('html')[0];
    background.style.backgroundColor = '#4a4f9b';
    const body = document.getElementsByTagName('body')[0];
    body.style.backgroundColor = '#fff';
    const nextId = document.getElementById('__next');
    nextId.style.padding = '3rem';
    axios
      .get(
        'https://baconipsum.com/api/?type=meat-and-filler&paras=4&format=text'
      )
      .then((response) => setPost(response.data));
  }, []);

  return (
    <>
      <header className={classes.Header}>
        <h1 className={classes.Title}> Blog page</h1>
        <nav>
          <ul className={classes.Ul}>
            <NavList navContent='About Lu' href='' link='/#about' />
            <NavList navContent='Contact' href='' link='/#contact' />
            <NavList navContent='Blog' href='' link='/blog' />
          </ul>
        </nav>
      </header>
      <h2>Blog</h2>

      <Layout pageTitle='Realtime Comments'>
        <main className='container-fluid position-absolute h-100 bg-white'>
          <div className='row position-absolute w-100 h-100'>
            <section className='col-md-8 d-flex flex-row flex-wrap align-items-center align-content-center border-right border-gray px-0'>
              {post && (
                <div className='position-relative h-100'>
                  <div className='px-5 mt-5 pt-5 mx-5'>
                    <span className='d-block px-5 mx-5 pt-5 h5 text-uppercase text-primary font-weight-bold mb-3'>
                      Editor's Pick
                    </span>
                    <span className='d-block px-5 mx-5 pb-5 h1 text-dark border-bottom border-gray'>
                      Getting Started with Lorem Ipsum
                    </span>
                  </div>

                  <div
                    className='d-block h-50 px-5 mt-5 pt-3 mx-5 position-relative'
                    style={{ overflowY: 'auto' }}
                  >
                    <span
                      className='d-block px-5 mx-5 text-secondary text-justify'
                      style={{ fontSize: '1rem', whiteSpace: 'pre-line' }}
                    >
                      {post}
                    </span>
                  </div>
                </div>
              )}
            </section>

            <section className='col-md-4 position-relative d-flex flex-wrap h-100 align-items-start align-content-between bg-light px-0'></section>
          </div>
        </main>
      </Layout>

      <p>
